import axios from "axios"


const runQuery = term => {
  let formattedTerm = term.trim()

  const url = `https://www.googleapis.com/books/v1/volumes?q=${formattedTerm}&maxResults=39`

  const error = new Error(); 
  return axios.get(url)
    .then(results => {
      if (!results.ok) throw error;
      return results.data
    })
}

export {runQuery};
